[select prtmst_view.*,
        short_dsc,
        lngdsc
   from prtmst_view
   left outer
   join prtdsc
     on prtdsc.colnam = 'prtnum|prt_client_id|wh_id_tmpl'
    and prtdsc.colval =
        /*=varchar(*/
        nvl(prtmst_view.prtnum, rtrim(' ')) || '|' || nvl(prtmst_view.prt_client_id, rtrim(' ')) || '|' || nvl(prtmst_view.wh_id_tmpl, rtrim(' '))
        /*=)*/
    and prtdsc.locale_id = nvl(@locale_id, @@locale_id)
  where prtnum in ('027242866065', '068888751223', '068888751230', '068888754019', '068888754033', '068888754040', '4894041004914', '4894041004938', '4894041005010', '4894041005027', '609207955191', '609207955207', '609207955238', '609224011825', '609224011832', '609224011849', '609224011856', '609224011863', '665129507535', '665129507566', '665129507573', '722630013563', '722630013624', '722630013693', '722630013754', '722630181156', '722630208471', '722630208488', '722630208532', '722630269274', '722630269700', '722630269908', '722630273189', '723175782280', '723175782297', '723755894846', '766419178654', '766419178661', '766419178791', '766419178807', '766419184068', '788678012982', '797581003154', '797581003239', '797581003260', '807995040048', '807995041502', '807995041847', '807995041953', '807995042929', '807995043094', '807995044282', '807995044572', '807995044916', '807995045098', '807995045104', '807995045128', '807995045173', '807995046194', '807995046200', '807995046224', '807995050252', '807995050870', '807995051068', '807995077181', '814781013117', '819850010097', '819850013210', '819850013258', '840081102107', '840081102114', '840344101281', '840344107535', '840344107542', '840344108716', '840344108778', '840344108785', '840344117213', '840344123252', '840344123269', '842591008573', '842591008603', '842591008610', '842591008979', '847864109230', '849813001299', '849813001312', '883121768185', '886678184897')]